'use client';

import { useState, useEffect } from 'react';
import { useParams, useRouter } from 'next/navigation';
import { useAuth } from '@/components/auth/AuthProvider';
import { createClient } from '@/lib/supabase/client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  ArrowLeft,
  Lock,
  Users,
  Calendar,
  MapPin,
  Camera,
  Loader2,
  AlertCircle,
  Eye,
  Edit,
  Plus
} from 'lucide-react';
import Link from 'next/link';
import { PhotoGrid } from '@/components/photos/PhotoGrid';
import { getShareByToken } from '@/app/actions/album-sharing';
import type { Album, Photo, AlbumShare, SharePermissionLevel } from '@/types/database';
import { log } from '@/lib/utils/logger';
import { LikeButton } from '@/components/social/LikeButton';
import { getPhotoUrl } from '@/lib/utils/photo-url';

export default function SharedAlbumPage() {
  const params = useParams();
  const router = useRouter();
  const { user } = useAuth();
  const [share, setShare] = useState<AlbumShare | null>(null);
  const [album, setAlbum] = useState<Album | null>(null);
  const [photos, setPhotos] = useState<Photo[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const supabase = createClient();

  useEffect(() => {
    loadSharedAlbum();
  }, [params.token]);

  const loadSharedAlbum = async () => {
    try {
      setLoading(true);
      setError(null);

      // Get share by token
      const shareResult = await getShareByToken(params.token as string);

      if (!shareResult.success || !shareResult.data) {
        throw new Error(shareResult.error || 'Share not found');
      }

      const shareData = shareResult.data as AlbumShare & {
        album?: Album;
        shared_by?: { username: string; display_name?: string; avatar_url?: string };
      };

      setShare(shareData);
      setAlbum(shareData.album || null);

      // Fetch photos
      if (shareData.album?.id) {
        const { data: photosData, error: photosError } = await supabase
          .from('photos')
          .select('*')
          .eq('album_id', shareData.album.id)
          .order('order_index', { ascending: true });

        if (photosError) {
          log.error('Failed to fetch photos', {
            component: 'SharedAlbumPage',
            albumId: shareData.album.id,
          }, photosError);
        } else {
          setPhotos(photosData || []);
        }
      }
    } catch (err) {
      log.error('Failed to load shared album', {
        component: 'SharedAlbumPage',
        token: params.token,
      }, err as Error);
      setError(err instanceof Error ? err.message : 'Failed to load shared album');
    } finally {
      setLoading(false);
    }
  };

  const getPermissionIcon = (level?: SharePermissionLevel) => {
    switch (level) {
      case 'view':
        return <Eye className="h-4 w-4" />;
      case 'contribute':
        return <Plus className="h-4 w-4" />;
      case 'edit':
        return <Edit className="h-4 w-4" />;
      default:
        return <Eye className="h-4 w-4" />;
    }
  };

  const getPermissionLabel = (level?: SharePermissionLevel) => {
    switch (level) {
      case 'view':
        return 'View Only';
      case 'contribute':
        return 'Can Contribute';
      case 'edit':
        return 'Can Edit';
      default:
        return 'View Only';
    }
  };

  const canContribute = share?.permission_level === 'contribute' || share?.permission_level === 'edit';
  const canEdit = share?.permission_level === 'edit';

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <Card className="max-w-md w-full">
          <CardContent className="py-12">
            <div className="flex flex-col items-center gap-4">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
              <p className="text-gray-600">Loading shared album...</p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (error || !share || !album) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <Card className="max-w-md w-full border-red-200 bg-red-50">
          <CardContent className="py-8">
            <div className="flex flex-col items-center gap-4 text-center">
              <div className="w-12 h-12 bg-red-100 rounded-full flex items-center justify-center">
                <AlertCircle className="h-6 w-6 text-red-600" />
              </div>
              <div>
                <h3 className="font-semibold text-lg text-red-900">Cannot Access Album</h3>
                <p className="text-red-700 mt-2">
                  {error || 'This share link may have expired or been revoked.'}
                </p>
              </div>
              <Link href="/albums">
                <Button variant="outline" className="mt-2">
                  <ArrowLeft className="h-4 w-4 mr-2" />
                  Back to Albums
                </Button>
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 pb-20">
      {/* Header */}
      <div className="bg-white border-b sticky top-0 z-10">
        <div className="max-w-5xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <Link href="/albums">
              <Button variant="ghost" size="sm">
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back
              </Button>
            </Link>
            <Badge variant="secondary" className="flex items-center gap-1">
              {getPermissionIcon(share.permission_level)}
              {getPermissionLabel(share.permission_level)}
            </Badge>
          </div>
        </div>
      </div>

      <div className="max-w-5xl mx-auto px-4 py-6 space-y-6">
        {/* Album Info */}
        <Card>
          <CardContent className="py-6">
            <div className="space-y-4">
              {/* Shared by notice */}
              <div className="flex items-center gap-2 text-sm text-gray-600 bg-blue-50 p-3 rounded-lg">
                <Users className="h-4 w-4 text-blue-600" />
                <span>
                  Shared by{' '}
                  <span className="font-medium text-blue-900">
                    {share.shared_by?.display_name || share.shared_by?.username || 'Someone'}
                  </span>
                </span>
              </div>

              {/* Title & Description */}
              <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-2">
                  {album.title}
                </h1>
                {album.description && (
                  <p className="text-gray-700 text-lg">{album.description}</p>
                )}
              </div>

              {/* Metadata */}
              <div className="flex flex-wrap gap-4 text-sm text-gray-600">
                {album.location_name && (
                  <div className="flex items-center gap-1">
                    <MapPin className="h-4 w-4" />
                    <span>{album.location_name}</span>
                  </div>
                )}

                {(album.date_start || album.date_end) && (
                  <div className="flex items-center gap-1">
                    <Calendar className="h-4 w-4" />
                    <span>
                      {album.date_start && new Date(album.date_start).toLocaleDateString()}
                      {album.date_end && album.date_start !== album.date_end &&
                        ` - ${new Date(album.date_end).toLocaleDateString()}`}
                    </span>
                  </div>
                )}

                <div className="flex items-center gap-1">
                  <Camera className="h-4 w-4" />
                  <span>{photos.length} photo{photos.length !== 1 ? 's' : ''}</span>
                </div>
              </div>

              {/* Actions */}
              <div className="flex items-center gap-3 pt-4 border-t">
                <LikeButton albumId={album.id} />

                {canContribute && (
                  <Link href={`/albums/${album.id}/upload`}>
                    <Button size="sm" variant="outline">
                      <Plus className="h-4 w-4 mr-2" />
                      Add Photos
                    </Button>
                  </Link>
                )}

                {canEdit && (
                  <Link href={`/albums/${album.id}/edit`}>
                    <Button size="sm" variant="outline">
                      <Edit className="h-4 w-4 mr-2" />
                      Edit Album
                    </Button>
                  </Link>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Photos Grid */}
        {photos.length > 0 ? (
          <PhotoGrid
            photos={photos}
            albumId={album.id}
          />
        ) : (
          <Card>
            <CardContent className="py-12 text-center">
              <Camera className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600">This album doesn&apos;t have any photos yet.</p>
              {canContribute && (
                <Link href={`/albums/${album.id}/upload`}>
                  <Button className="mt-4">
                    <Plus className="h-4 w-4 mr-2" />
                    Add Photos
                  </Button>
                </Link>
              )}
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'

export async function GET(request: NextRequest) {
  // SECURITY: Require authentication to prevent unauthorized API access
  const supabase = await createClient()
  const { data: { user }, error: authError } = await supabase.auth.getUser()

  if (authError || !user) {
    return NextResponse.json(
      { error: 'Unauthorized - authentication required' },
      { status: 401 }
    )
  }

  const { searchParams } = new URL(request.url)
  const query = searchParams.get('q')
  const lat = searchParams.get('lat')
  const lon = searchParams.get('lon')
  const reverse = searchParams.get('reverse')

  try {
    let nominatimUrl: string

    if (reverse === 'true' && lat && lon) {
      // Reverse geocoding
      nominatimUrl = `https://nominatim.openstreetmap.org/reverse?` +
        new URLSearchParams({
          lat,
          lon,
          format: 'json',
          'accept-language': 'en'
        }).toString()
    } else if (query) {
      // Forward geocoding
      nominatimUrl = `https://nominatim.openstreetmap.org/search?` +
        new URLSearchParams({
          q: query,
          format: 'json',
          limit: '8',
          dedupe: '1',
          'accept-language': 'en',
          addressdetails: '1'
        }).toString()
    } else {
      return NextResponse.json(
        { error: 'Missing required parameters' },
        { status: 400 }
      )
    }

    const response = await fetch(nominatimUrl, {
      headers: {
        'User-Agent': 'Adventure Log App (contact@example.com)'
      }
    })

    if (!response.ok) {
      throw new Error(`OpenStreetMap API error: ${response.status}`)
    }

    const data = await response.json()

    return NextResponse.json(data, {
      headers: {
        'Cache-Control': 'public, max-age=300', // 5 minutes cache
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Methods': 'GET',
        'Access-Control-Allow-Headers': 'Content-Type'
      }
    })
  } catch (error) {
    // Use proper error handling without exposing internal details
    return NextResponse.json(
      { error: 'Failed to fetch location data' },
      { status: 500 }
    )
  }
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/components/auth/AuthProvider';
import { createClient } from '@/lib/supabase/client';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Sparkles, Check, X, Calendar, MapPin, Camera, Loader2 } from 'lucide-react';
import Image from 'next/image';
import { getPhotoUrl } from '@/lib/utils/photo-url';
import { generateAlbumSuggestions, filterExistingAlbums, type AlbumSuggestion } from '@/lib/ai/album-suggestions';
import type { Photo } from '@/types/database';
import { log } from '@/lib/utils/logger';
import { Toast } from '@capacitor/toast';

export function AlbumSuggestions() {
  const { user } = useAuth();
  const router = useRouter();
  const [suggestions, setSuggestions] = useState<AlbumSuggestion[]>([]);
  const [loading, setLoading] = useState(true);
  const [creating, setCreating] = useState<string | null>(null);
  const supabase = createClient();

  useEffect(() => {
    if (user) {
      loadSuggestions();
    }
  }, [user]);

  const loadSuggestions = async () => {
    try {
      setLoading(true);

      // Fetch all user photos
      const { data: photos, error: photosError } = await supabase
        .from('photos')
        .select('*')
        .eq('user_id', user!.id)
        .not('taken_at', 'is', null);

      if (photosError) throw photosError;

      // Fetch existing album photo IDs to filter out
      const { data: existingPhotos } = await supabase
        .from('photos')
        .select('id')
        .eq('user_id', user!.id)
        .not('album_id', 'is', null);

      const existingPhotoIds = new Set(existingPhotos?.map(p => p.id) || []);

      // Generate suggestions
      const allSuggestions = generateAlbumSuggestions(photos || []);
      const filteredSuggestions = filterExistingAlbums(allSuggestions, existingPhotoIds);

      // Take top 5 suggestions
      setSuggestions(filteredSuggestions.slice(0, 5));
    } catch (error) {
      log.error('Failed to generate album suggestions', { component: 'AlbumSuggestions' }, error as Error);
    } finally {
      setLoading(false);
    }
  };

  const createAlbumFromSuggestion = async (suggestion: AlbumSuggestion) => {
    setCreating(suggestion.id);

    try {
      // Create album
      const { data: album, error: albumError } = await supabase
        .from('albums')
        .insert({
          user_id: user!.id,
          title: suggestion.suggested_title,
          description: suggestion.suggested_description,
          date_start: suggestion.start_date,
          date_end: suggestion.end_date,
          location_name: suggestion.location_name,
          location_lat: suggestion.latitude,
          location_lng: suggestion.longitude,
          visibility: 'public',
          status: 'published',
        })
        .select()
        .single();

      if (albumError) throw albumError;

      // Assign photos to album
      const photoUpdates = suggestion.photos.map((photo, index) => ({
        id: photo.id,
        album_id: album.id,
        order_index: index,
      }));

      for (const update of photoUpdates) {
        await supabase
          .from('photos')
          .update({
            album_id: update.album_id,
            order_index: update.order_index,
          })
          .eq('id', update.id);
      }

      // Set cover photo
      if (suggestion.photos.length > 0) {
        await supabase
          .from('albums')
          .update({
            cover_photo_url: suggestion.photos[0].file_path,
          })
          .eq('id', album.id);
      }

      await Toast.show({
        text: `Album "${suggestion.suggested_title}" created!`,
        duration: 'short',
      });

      // Remove suggestion from list
      setSuggestions(suggestions.filter(s => s.id !== suggestion.id));

      // Navigate to album
      router.push(`/albums/${album.id}`);
    } catch (error) {
      log.error('Failed to create album from suggestion', { component: 'AlbumSuggestions' }, error as Error);
      await Toast.show({
        text: 'Failed to create album',
        duration: 'short',
      });
    } finally {
      setCreating(null);
    }
  };

  const dismissSuggestion = (suggestionId: string) => {
    setSuggestions(suggestions.filter(s => s.id !== suggestionId));
  };

  if (loading) {
    return (
      <Card>
        <CardContent className="py-8">
          <div className="flex items-center justify-center gap-2">
            <Loader2 className="h-5 w-5 animate-spin" />
            <span>Analyzing your photos...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (suggestions.length === 0) {
    return null;
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center gap-2">
        <Sparkles className="h-5 w-5 text-purple-600" />
        <h2 className="text-xl font-bold">Album Suggestions</h2>
        <Badge variant="secondary" className="ml-2">
          AI-Generated
        </Badge>
      </div>

      <p className="text-sm text-gray-600">
        We found {suggestions.length} potential album{suggestions.length > 1 ? 's' : ''} based on your photos&apos; dates and locations.
      </p>

      <div className="grid gap-4">
        {suggestions.map((suggestion) => (
          <Card key={suggestion.id} className="overflow-hidden">
            <CardHeader className="pb-3">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <CardTitle className="text-lg">{suggestion.suggested_title}</CardTitle>
                  <CardDescription className="mt-1">
                    {suggestion.suggested_description}
                  </CardDescription>
                </div>
                <Badge
                  variant={
                    suggestion.confidence_score >= 80
                      ? 'default'
                      : suggestion.confidence_score >= 60
                      ? 'secondary'
                      : 'outline'
                  }
                >
                  {suggestion.confidence_score}% match
                </Badge>
              </div>
            </CardHeader>

            <CardContent className="space-y-4">
              {/* Metadata */}
              <div className="flex flex-wrap gap-4 text-sm text-gray-600">
                {suggestion.start_date && (
                  <div className="flex items-center gap-1">
                    <Calendar className="h-4 w-4" />
                    <span>
                      {new Date(suggestion.start_date).toLocaleDateString()} - {new Date(suggestion.end_date!).toLocaleDateString()}
                    </span>
                  </div>
                )}
                {suggestion.location_name && (
                  <div className="flex items-center gap-1">
                    <MapPin className="h-4 w-4" />
                    <span>{suggestion.location_name}</span>
                  </div>
                )}
                <div className="flex items-center gap-1">
                  <Camera className="h-4 w-4" />
                  <span>{suggestion.photos.length} photos</span>
                </div>
              </div>

              {/* Photo preview */}
              <div className="grid grid-cols-4 gap-2">
                {suggestion.photos.slice(0, 4).map((photo, index) => (
                  <div key={photo.id} className="relative aspect-square">
                    <Image
                      src={getPhotoUrl(photo.file_path) || ''}
                      alt={`Preview ${index + 1}`}
                      fill
                      className="object-cover rounded"
                    />
                  </div>
                ))}
                {suggestion.photos.length > 4 && (
                  <div className="relative aspect-square bg-gray-100 rounded flex items-center justify-center">
                    <span className="text-sm text-gray-600">
                      +{suggestion.photos.length - 4} more
                    </span>
                  </div>
                )}
              </div>

              {/* Actions */}
              <div className="flex items-center gap-2 pt-2">
                <Button
                  onClick={() => createAlbumFromSuggestion(suggestion)}
                  disabled={creating !== null}
                  className="flex-1"
                >
                  {creating === suggestion.id ? (
                    <>
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      Creating...
                    </>
                  ) : (
                    <>
                      <Check className="h-4 w-4 mr-2" />
                      Create Album
                    </>
                  )}
                </Button>
                <Button
                  variant="outline"
                  onClick={() => dismissSuggestion(suggestion.id)}
                  disabled={creating !== null}
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>

              {/* Reason */}
              <p className="text-xs text-gray-500 italic">
                Suggested because: {suggestion.reason}
              </p>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Copy, Check, X, UserPlus, Link as LinkIcon, Trash2, Settings, Clock } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { createAlbumShare, getAlbumShares, deleteAlbumShare, updateAlbumShare } from '@/app/actions/album-sharing';
import type { AlbumShare, SharePermissionLevel } from '@/types/database';
import { log } from '@/lib/utils/logger';
import { Toast } from '@capacitor/toast';

interface ShareAlbumDialogProps {
  albumId: string;
  albumTitle: string;
  trigger?: React.ReactNode;
}

export function ShareAlbumDialog({ albumId, albumTitle, trigger }: ShareAlbumDialogProps) {
  const [open, setOpen] = useState(false);
  const [shares, setShares] = useState<AlbumShare[]>([]);
  const [loading, setLoading] = useState(false);
  const [copied, setCopied] = useState(false);

  // Form state
  const [email, setEmail] = useState('');
  const [permissionLevel, setPermissionLevel] = useState<SharePermissionLevel>('view');
  const [expirationDays, setExpirationDays] = useState<string>('never');

  // Load existing shares
  useEffect(() => {
    if (open) {
      loadShares();
    }
  }, [open]);

  const loadShares = async () => {
    const result = await getAlbumShares(albumId);
    if (result.success && result.data) {
      setShares(result.data);
    }
  };

  const handleCreateShare = async () => {
    if (!email && expirationDays === 'never') {
      // Create public link
      await createPublicShare();
    } else {
      // Create user-specific share
      await createUserShare();
    }
  };

  const createPublicShare = async () => {
    setLoading(true);
    try {
      const result = await createAlbumShare({
        album_id: albumId,
        permission_level: permissionLevel,
      });

      if (result.success && result.data) {
        await Toast.show({
          text: 'Share link created successfully!',
          duration: 'short',
        });
        setShares([...shares, result.data]);
        copyShareLink(result.data.share_token);
      } else {
        throw new Error(result.error || 'Failed to create share');
      }
    } catch (error) {
      log.error('Failed to create share', { component: 'ShareAlbumDialog' }, error as Error);
      await Toast.show({
        text: 'Failed to create share link',
        duration: 'short',
      });
    } finally {
      setLoading(false);
    }
  };

  const createUserShare = async () => {
    if (!email) {
      await Toast.show({
        text: 'Please enter an email address',
        duration: 'short',
      });
      return;
    }

    setLoading(true);
    try {
      const expiresAt = expirationDays !== 'never'
        ? new Date(Date.now() + parseInt(expirationDays) * 24 * 60 * 60 * 1000).toISOString()
        : undefined;

      const result = await createAlbumShare({
        album_id: albumId,
        shared_with_email: email,
        permission_level: permissionLevel,
        expires_at: expiresAt,
      });

      if (result.success && result.data) {
        await Toast.show({
          text: `Album shared with ${email}!`,
          duration: 'short',
        });
        setShares([...shares, result.data]);
        setEmail('');
      } else {
        throw new Error(result.error || 'Failed to share album');
      }
    } catch (error) {
      log.error('Failed to share album', { component: 'ShareAlbumDialog' }, error as Error);
      await Toast.show({
        text: error instanceof Error ? error.message : 'Failed to share album',
        duration: 'long',
      });
    } finally {
      setLoading(false);
    }
  };

  const copyShareLink = (token: string) => {
    const url = `${window.location.origin}/albums/shared/${token}`;
    navigator.clipboard.writeText(url);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const handleDeleteShare = async (shareId: string) => {
    const result = await deleteAlbumShare(shareId);
    if (result.success) {
      setShares(shares.filter(s => s.id !== shareId));
      await Toast.show({
        text: 'Share revoked successfully',
        duration: 'short',
      });
    }
  };

  const getPermissionLabel = (level: SharePermissionLevel) => {
    switch (level) {
      case 'view':
        return 'Can view';
      case 'contribute':
        return 'Can add photos';
      case 'edit':
        return 'Can edit';
      default:
        return level;
    }
  };

  const getPermissionColor = (level: SharePermissionLevel) => {
    switch (level) {
      case 'view':
        return 'bg-gray-100 text-gray-800';
      case 'contribute':
        return 'bg-blue-100 text-blue-800';
      case 'edit':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {trigger || (
          <Button variant="outline" size="sm">
            <UserPlus className="h-4 w-4 mr-2" />
            Share
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Share &quot;{albumTitle}&quot;</DialogTitle>
          <DialogDescription>
            Invite others to view or collaborate on this album
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Create new share */}
          <div className="space-y-4 p-4 border border-gray-200 rounded-lg bg-gray-50">
            <h3 className="font-semibold text-sm">Invite someone</h3>

            <div className="space-y-2">
              <Label htmlFor="email">Email address (optional for link sharing)</Label>
              <Input
                id="email"
                type="email"
                placeholder="friend@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="permission">Permission level</Label>
                <Select
                  value={permissionLevel}
                  onValueChange={(value) => setPermissionLevel(value as SharePermissionLevel)}
                >
                  <SelectTrigger id="permission">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="view">
                      <div className="flex flex-col items-start">
                        <span className="font-medium">Can view</span>
                        <span className="text-xs text-gray-500">View photos only</span>
                      </div>
                    </SelectItem>
                    <SelectItem value="contribute">
                      <div className="flex flex-col items-start">
                        <span className="font-medium">Can contribute</span>
                        <span className="text-xs text-gray-500">Add photos and comments</span>
                      </div>
                    </SelectItem>
                    <SelectItem value="edit">
                      <div className="flex flex-col items-start">
                        <span className="font-medium">Can edit</span>
                        <span className="text-xs text-gray-500">Full access (add, delete, edit)</span>
                      </div>
                    </SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="expiration">Expires in</Label>
                <Select value={expirationDays} onValueChange={setExpirationDays}>
                  <SelectTrigger id="expiration">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="never">Never</SelectItem>
                    <SelectItem value="1">1 day</SelectItem>
                    <SelectItem value="7">7 days</SelectItem>
                    <SelectItem value="30">30 days</SelectItem>
                    <SelectItem value="90">90 days</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <Button
              onClick={handleCreateShare}
              disabled={loading}
              className="w-full"
            >
              {loading ? (
                'Creating...'
              ) : email ? (
                <>
                  <UserPlus className="h-4 w-4 mr-2" />
                  Send invitation
                </>
              ) : (
                <>
                  <LinkIcon className="h-4 w-4 mr-2" />
                  Create share link
                </>
              )}
            </Button>
          </div>

          {/* Existing shares */}
          {shares.length > 0 && (
            <div className="space-y-3">
              <h3 className="font-semibold text-sm">Active shares ({shares.length})</h3>
              <div className="space-y-2">
                {shares.map((share) => (
                  <div
                    key={share.id}
                    className="flex items-center justify-between p-3 border border-gray-200 rounded-lg hover:bg-gray-50"
                  >
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2">
                        <span className="font-medium text-sm truncate">
                          {share.shared_with?.email || share.shared_with?.username || 'Anyone with link'}
                        </span>
                        <Badge className={getPermissionColor(share.permission_level)}>
                          {getPermissionLabel(share.permission_level)}
                        </Badge>
                        {share.expires_at && (
                          <Badge variant="outline" className="text-xs">
                            <Clock className="h-3 w-3 mr-1" />
                            Expires {new Date(share.expires_at).toLocaleDateString()}
                          </Badge>
                        )}
                      </div>
                      {!share.shared_with_user_id && (
                        <button
                          onClick={() => copyShareLink(share.share_token)}
                          className="text-xs text-blue-600 hover:underline mt-1 flex items-center gap-1"
                        >
                          {copied ? (
                            <>
                              <Check className="h-3 w-3" />
                              Copied!
                            </>
                          ) : (
                            <>
                              <Copy className="h-3 w-3" />
                              Copy link
                            </>
                          )}
                        </button>
                      )}
                    </div>

                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleDeleteShare(share.id)}
                      className="text-red-600 hover:text-red-700 hover:bg-red-50"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

'use client'

import { useRef, useEffect, useState, useMemo, useCallback } from 'react'
import { flushSync } from 'react-dom'
import dynamic from 'next/dynamic'
import type { GlobeMethods } from 'react-globe.gl'
import { useTravelTimeline, type TravelLocation, type Album } from '@/lib/hooks/useTravelTimeline'
import { useFlightAnimation } from '@/lib/hooks/useFlightAnimation'
import { FlightAnimation } from './FlightAnimation'
import { CityPinSystem, formatPinTooltip, type CityPin, type CityCluster } from './CityPinSystem'
import { AlbumImageModal } from './AlbumImageModal'
import type { GlobeInstance, GlobeHtmlElement } from '@/types/globe'
import { GlobeSearch, type GlobeSearchResult } from './GlobeSearch'
import { Card, CardContent } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import {
  Globe as GlobeIcon,
  Plus,
  Loader2,
  RotateCcw,
  Play,
  Pause,
  Plane,
  Route,
  Search,
  ZoomIn,
  ZoomOut
} from 'lucide-react'
import Link from 'next/link'
import { log } from '@/lib/utils/logger'
import { cn } from '@/lib/utils'
import { escapeHtml, escapeAttr } from '@/lib/utils/html-escape'

// Dynamically import the Globe component to avoid SSR issues
const Globe = dynamic(() => import('react-globe.gl'), { ssr: false })

interface FlightPath {
  startLat: number
  startLng: number
  endLat: number
  endLng: number
  color: string
  year: number
  name: string
}

// Type definitions for accessing Three.js renderer internals
interface GlobeInternals {
  scene?: () => unknown
  renderer?: () => ThreeRenderer | undefined
}

interface ThreeRenderer {
  setAnimationLoop: (callback: ((time: number) => void) | null) => void
}

interface EnhancedGlobeProps {
  className?: string
  initialAlbumId?: string
  initialLat?: number
  initialLng?: number
  filterUserId?: string
}

export function EnhancedGlobe({ className, initialAlbumId, initialLat, initialLng, filterUserId }: EnhancedGlobeProps) {
  const globeRef = useRef<GlobeMethods | undefined>(undefined)
  const [globeReady, setGlobeReady] = useState(false)
  const [selectedCluster, setSelectedCluster] = useState<CityCluster | null>(null)
  const [showAlbumModal, setShowAlbumModal] = useState(false)
  const [activeCityId, setActiveCityId] = useState<string | null>(null)
  const [isAutoRotating, setIsAutoRotating] = useState(false) // Disabled by default for better performance
  const [userInteracting, setUserInteracting] = useState(false)
  const [showSearch, setShowSearch] = useState(false)
  const [windowDimensions, setWindowDimensions] = useState({ width: 800, height: 500 })
  const [currentAlbumIndex, setCurrentAlbumIndex] = useState(0)
  const [showStaticConnections, setShowStaticConnections] = useState(true)
  const [progressionMode, setProgressionMode] = useState<'auto' | 'manual'>('auto')
  const [currentLocationIndex, setCurrentLocationIndex] = useState(0)
  const [isJourneyPaused, setIsJourneyPaused] = useState(false)
  const autoRotateRef = useRef<NodeJS.Timeout | null>(null)
  const cameraAnimationRef = useRef<number | null>(null)
  const initialNavigationHandled = useRef(false)
  const globeContainerRef = useRef<HTMLDivElement | null>(null)
  const isVisibleRef = useRef(true)
  const isInViewportRef = useRef(true)
  const rendererRef = useRef<ThreeRenderer | null>(null)

  // Performance settings - automatically optimized based on hardware detection
  const [performanceMode, setPerformanceMode] = useState<'auto' | 'high' | 'balanced' | 'low'>('auto')
  const [hardwareAcceleration, setHardwareAcceleration] = useState<boolean | null>(null)

  // Helper function to check if rendering should be active
  const shouldRender = useCallback(() => {
    return isVisibleRef.current && isInViewportRef.current
  }, [])

  // Page Visibility API - Pause rendering when tab is inactive
  useEffect(() => {
    const handleVisibilityChange = () => {
      const isVisible = !document.hidden
      isVisibleRef.current = isVisible

      log.info('Page visibility changed', {
        component: 'EnhancedGlobe',
        action: 'visibility-change',
        isVisible
      })

      if (!isVisible) {
        // Pause all animations when tab is hidden
        if (isAutoRotating) {
          setIsAutoRotating(false)
        }

        // Stop WebGL rendering
        if (rendererRef.current) {
          rendererRef.current.setAnimationLoop(null)
          log.info('Stopped WebGL rendering (tab hidden)', { component: 'EnhancedGlobe' })
        }
      } else {
        // Resume rendering when tab becomes visible
        if (rendererRef.current && shouldRender()) {
          // Re-enable animation loop
          const globeMethods = globeRef.current as unknown as GlobeInternals
          if (globeMethods.renderer) {
            const renderer = globeMethods.renderer()
            if (renderer) {
              // Restart the throttled animation loop
              log.info('Resumed WebGL rendering (tab visible)', { component: 'EnhancedGlobe' })
            }
          }
        }
      }
    }

    document.addEventListener('visibilitychange', handleVisibilityChange)

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange)
    }
  }, [isAutoRotating, shouldRender])

  // Intersection Observer - Pause rendering when globe is out of viewport
  useEffect(() => {
    if (!globeContainerRef.current) return

    const observerOptions = {
      root: null,
      rootMargin: '100px', // Start rendering slightly before visible
      threshold: 0.1 // Trigger when 10% visible
    }

    const handleIntersection = (entries: IntersectionObserverEntry[]) => {
      entries.forEach(entry => {
        const isInViewport = entry.isIntersecting
        isInViewportRef.current = isInViewport

        log.info('Globe viewport visibility changed', {
          component: 'EnhancedGlobe',
          action: 'viewport-change',
          isInViewport,
          intersectionRatio: entry.intersectionRatio
        })

        if (!isInViewport) {
          // Pause animations when out of viewport
          if (isAutoRotating) {
            setIsAutoRotating(false)
          }

          // Stop WebGL rendering
          if (rendererRef.current) {
            rendererRef.current.setAnimationLoop(null)
            log.info('Stopped WebGL rendering (out of viewport)', { component: 'EnhancedGlobe' })
          }
        } else if (shouldRender()) {
          // Resume rendering when back in viewport and tab is visible
          if (rendererRef.current) {
            log.info('Resumed WebGL rendering (in viewport)', { component: 'EnhancedGlobe' })
          }
        }
      })
    }

    const observer = new IntersectionObserver(handleIntersection, observerOptions)
    observer.observe(globeContainerRef.current)

    return () => {
      observer.disconnect()
    }
  }, [isAutoRotating, shouldRender])

  // Detect hardware acceleration
  useEffect(() => {
    const detectHardwareAcceleration = () => {
      try {
        const canvas = document.createElement('canvas')
        const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl')

        if (!gl) {
          setHardwareAcceleration(false)
          // Auto-switch to low mode for better performance
          setPerformanceMode('low')
          log.warn('WebGL not available, using low performance mode')
          return
        }

        const debugInfo = (gl as WebGLRenderingContext).getExtension('WEBGL_debug_renderer_info')
        if (debugInfo) {
          const renderer = (gl as WebGLRenderingContext).getParameter(debugInfo.UNMASKED_RENDERER_WEBGL)
          // Check if using software renderer
          const isSoftware = /SwiftShader|llvmpipe|Microsoft Basic Render Driver/i.test(renderer)
          setHardwareAcceleration(!isSoftware)

          if (isSoftware) {
            // Auto-switch to low mode for software rendering
            setPerformanceMode('low')
            log.warn('Software rendering detected, using low performance mode', { renderer })
          } else {
            log.info('Hardware acceleration detected', { renderer })
          }
        } else {
          // Can't detect, assume hardware acceleration is available
          setHardwareAcceleration(true)
        }
      } catch (error) {
        log.error('Failed to detect hardware acceleration', { error })
        setHardwareAcceleration(true)
      }
    }

    detectHardwareAcceleration()
  }, [])


  // Handle window resize for responsive globe - Heavily throttled for performance
  useEffect(() => {
    let resizeTimeout: NodeJS.Timeout | null = null
    let idleCallbackId: number | null = null

    const updateDimensions = () => {
      const width = Math.min(window.innerWidth * 0.9, 1200)
      const height = window.innerWidth < 640 ? Math.max(window.innerHeight * 0.6, 500) :
                    window.innerWidth < 1024 ? Math.max(window.innerHeight * 0.65, 650) :
                    window.innerWidth < 1440 ? Math.max(window.innerHeight * 0.75, 750) :
                    Math.max(window.innerHeight * 0.8, 800)

      // Only update if dimensions changed significantly (>10px to avoid jitter)
      setWindowDimensions(prev => {
        if (Math.abs(prev.width - width) > 10 || Math.abs(prev.height - height) > 10) {
          return { width, height }
        }
        return prev
      })
    }

    // Use requestIdleCallback for non-critical dimension updates
    const throttledResize = () => {
      if (resizeTimeout) {
        clearTimeout(resizeTimeout)
      }
      if (idleCallbackId) {
        cancelIdleCallback(idleCallbackId)
      }

      // Debounce with timeout, then schedule update during idle time
      resizeTimeout = setTimeout(() => {
        if (typeof requestIdleCallback !== 'undefined') {
          // Use requestIdleCallback when available (better CPU efficiency)
          idleCallbackId = requestIdleCallback(() => {
            updateDimensions()
            idleCallbackId = null
          }, { timeout: 1000 })
        } else {
          // Fallback for browsers without requestIdleCallback
          updateDimensions()
        }
      }, 750)
    }

    updateDimensions()
    window.addEventListener('resize', throttledResize, { passive: true })
    return () => {
      window.removeEventListener('resize', throttledResize)
      if (resizeTimeout) clearTimeout(resizeTimeout)
      if (idleCallbackId && typeof cancelIdleCallback !== 'undefined') {
        cancelIdleCallback(idleCallbackId)
      }
    }
  }, [])


  // Calculate effective performance mode
  const effectivePerformanceMode = useMemo(() => {
    if (performanceMode !== 'auto') return performanceMode
    // Auto mode: use low if no hardware acceleration, balanced otherwise
    return hardwareAcceleration === false ? 'low' : 'balanced'
  }, [performanceMode, hardwareAcceleration])

  // Performance settings based on mode
  const performanceConfig = useMemo(() => {
    switch (effectivePerformanceMode) {
      case 'high':
        return {
          showAtmosphere: true,
          atmosphereOpacity: 0.8,
          atmosphereAltitude: 0.25,
          arcStroke: 3,
          showArcs: true,
          pinSize: 1.2,
          maxPins: 1000
        }
      case 'balanced':
        return {
          showAtmosphere: true,
          atmosphereOpacity: 0.6,
          atmosphereAltitude: 0.15,
          arcStroke: 2,
          showArcs: true,
          pinSize: 1.0,
          maxPins: 500
        }
      case 'low':
        return {
          showAtmosphere: false,
          atmosphereOpacity: 0,
          atmosphereAltitude: 0,
          arcStroke: 1,
          showArcs: false,
          pinSize: 0.8,
          maxPins: 200
        }
      default:
        return {
          showAtmosphere: true,
          atmosphereOpacity: 0.6,
          atmosphereAltitude: 0.15,
          arcStroke: 2,
          showArcs: true,
          pinSize: 1.0,
          maxPins: 500
        }
    }
  }, [effectivePerformanceMode])

  // Travel timeline hook
  const {
    availableYears,
    loading: timelineLoading,
    error: timelineError,
    selectedYear,
    setSelectedYear,
    refreshData,
    getYearData
  } = useTravelTimeline(filterUserId)

  // Get locations - show all years if no year is selected, otherwise filter by year
  const locations = useMemo(() => {
    if (selectedYear) {
      // Filter by selected year
      const yearData = getYearData(selectedYear)
      return yearData?.locations || []
    } else {
      // Show all years - combine all locations from all years
      const allLocations: TravelLocation[] = []
      availableYears.forEach(year => {
        const yearData = getYearData(year)
        if (yearData?.locations) {
          allLocations.push(...yearData.locations)
        }
      })
      return allLocations
    }
  }, [selectedYear, availableYears, getYearData])

  // Stable flight animation callbacks
  const handleSegmentComplete = useCallback((location: TravelLocation) => {
    setActiveCityId(location.id)

    // Update current location index
    const locationIndex = locations.findIndex(loc => loc.id === location.id)
    if (locationIndex !== -1) {
      setCurrentLocationIndex(locationIndex)
    }

    // In manual mode, pause the journey at each location
    if (progressionMode === 'manual') {
      setIsJourneyPaused(true)
      if (isPlaying) {
        pause()
      }
    }

    log.debug('Flight animation segment completed', {
      component: 'EnhancedGlobe',
      action: 'segment-complete',
      locationId: location.id,
      locationName: location.name,
      progressionMode,
      locationIndex
    })

    // Show album when flight segment completes
    const delay = progressionMode === 'manual' ? 500 : 1500
    setTimeout(() => {
      // Find albums and photos for this location
      const locationAlbums = location.albums || []
      const locationPhotos = location.photos || []

      if (locationAlbums.length > 0) {
        // Create a cluster for this location to show in the modal with unique ID
        const cluster: CityCluster = {
          id: `location-${location.id}-${Date.now()}`,
          latitude: location.latitude,
          longitude: location.longitude,
          cities: [{
            id: location.id,
            name: location.name,
            latitude: location.latitude,
            longitude: location.longitude,
            albumCount: locationAlbums.length,
            photoCount: locationPhotos.length,
            visitDate: location.visitDate.toISOString(),
            isVisited: true,
            isActive: true,
            favoritePhotoUrls: locationAlbums.flatMap(album => album.favoritePhotoUrls || []).slice(0, 3),
            coverPhotoUrl: locationAlbums[0]?.coverPhotoUrl,
            previewPhotoUrls: locationPhotos.map(p => p.url).filter((url): url is string => !!url)
          }],
          totalAlbums: locationAlbums.length,
          totalPhotos: locationPhotos.length,
          radius: 1
        }

        // Show the album modal
        setSelectedCluster(cluster)
        setShowAlbumModal(true)

        log.debug('Showing album for completed flight segment', {
          component: 'EnhancedGlobe',
          action: 'show-album',
          locationId: location.id,
          albumCount: locationAlbums.length,
          photoCount: locationPhotos.length,
          progressionMode
        })
      }
    }, delay)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [progressionMode, locations])

  // Animation complete callback (defined after locations)
  const handleAnimationComplete = useCallback(() => {
    // Focus on the final destination when animation completes
    if (locations.length > 0) {
      const finalDestination = locations[locations.length - 1]
      // Direct globe manipulation to avoid dependency issues
      if (globeRef.current) {
        const targetPOV = {
          lat: finalDestination.latitude,
          lng: finalDestination.longitude,
          altitude: 1.5
        }

        // Simple animation to final destination
        globeRef.current.pointOfView(targetPOV, 2000)
      }
      setActiveCityId(finalDestination.id)
    }
    log.info('Flight animation completed successfully', {
      component: 'EnhancedGlobe',
      action: 'animation-complete'
    })
  }, [locations])

  const handleAnimationError = useCallback((error: string) => {
    log.error('Flight animation failed', {
      component: 'EnhancedGlobe',
      action: 'animation-error',
      error: error
    })
  }, [])

  // Flight animation hook (defined after all callbacks)
  const {
    isPlaying,
    currentFlightState,
    cameraPosition,
    destinationCameraPosition,
    play,
    pause,
    reset,
    setLocations
  } = useFlightAnimation({
    autoPlay: false,
    defaultSpeed: 1,
    cameraFollowsPlane: true,
    onSegmentComplete: handleSegmentComplete,
    onAnimationComplete: handleAnimationComplete,
    onError: handleAnimationError
  })

  // Helper function to properly interpolate longitude (handling 180/-180 boundary)
  const interpolateLongitude = useCallback((start: number, end: number, progress: number) => {
    const diff = end - start
    const wrappedDiff = diff > 180 ? diff - 360 : diff < -180 ? diff + 360 : diff
    return start + wrappedDiff * progress
  }, [])

  // Calculate optimal camera position for locations
  const calculateOptimalCameraPosition = useCallback((locations: TravelLocation[]) => {
    if (locations.length === 0) return { lat: 0, lng: 0, altitude: 2.5 }
    if (locations.length === 1) {
      return {
        lat: locations[0].latitude,
        lng: locations[0].longitude,
        altitude: 1.8
      }
    }

    // Calculate bounds
    const lats = locations.map(loc => loc.latitude)
    const lngs = locations.map(loc => loc.longitude)
    const minLat = Math.min(...lats)
    const maxLat = Math.max(...lats)
    const minLng = Math.min(...lngs)
    const maxLng = Math.max(...lngs)

    // Calculate center and span
    const centerLat = (minLat + maxLat) / 2
    const centerLng = (minLng + maxLng) / 2
    const latSpan = maxLat - minLat
    const lngSpan = maxLng - minLng
    const maxSpan = Math.max(latSpan, lngSpan)

    // Calculate appropriate altitude based on span
    let altitude = 2.5
    if (maxSpan < 5) altitude = 2.0
    else if (maxSpan < 15) altitude = 2.2
    else if (maxSpan < 30) altitude = 2.5
    else if (maxSpan < 60) altitude = 3.0
    else altitude = 3.5

    return {
      lat: centerLat,
      lng: centerLng,
      altitude
    }
  }, [])

  // Enhanced camera animation function
  const animateCameraToPosition = useCallback((targetPOV: { lat: number; lng: number; altitude: number }, duration: number = 1000, easing: string = 'easeInOutQuad') => {
    if (!globeRef.current) return

    if (cameraAnimationRef.current) {
      cancelAnimationFrame(cameraAnimationRef.current)
    }

    const startPOV = globeRef.current.pointOfView()
    const startTime = Date.now()

    const easingFunctions = {
      linear: (t: number) => t,
      easeInOutQuad: (t: number) => t < 0.5 ? 2 * t * t : -1 + (4 - 2 * t) * t,
      easeInOutCubic: (t: number) => t < 0.5 ? 4 * t * t * t : (t - 1) * (2 * t - 2) * (2 * t - 2) + 1,
      easeInOutExpo: (t: number) => t === 0 ? 0 : t === 1 ? 1 : t < 0.5 ? Math.pow(2, 20 * t - 10) / 2 : (2 - Math.pow(2, -20 * t + 10)) / 2
    }

    const easeFn = easingFunctions[easing as keyof typeof easingFunctions] || easingFunctions.easeInOutQuad

    const animate = () => {
      const elapsed = Date.now() - startTime
      const progress = Math.min(elapsed / duration, 1)
      const easedProgress = easeFn(progress)

      // Interpolate position with proper longitude wrapping
      const interpolatedPOV = {
        lat: startPOV.lat + (targetPOV.lat - startPOV.lat) * easedProgress,
        lng: interpolateLongitude(startPOV.lng, targetPOV.lng, easedProgress),
        altitude: startPOV.altitude + (targetPOV.altitude - startPOV.altitude) * easedProgress
      }

      if (globeRef.current) {
        globeRef.current.pointOfView(interpolatedPOV, 0)
      }

      if (progress < 1) {
        cameraAnimationRef.current = requestAnimationFrame(animate)
      }
    }

    animate()
  }, [interpolateLongitude])

  // UI control handlers
  const handleYearChange = useCallback((year: number) => {
    setSelectedYear(year)
    setActiveCityId(null)
    setSelectedCluster(null)
    reset()

    // Smooth transition to optimal view for new year's locations
    setTimeout(() => {
      const yearData = getYearData(year)
      if (yearData && yearData.locations.length > 0) {
        const optimalPosition = calculateOptimalCameraPosition(yearData.locations)
        animateCameraToPosition(optimalPosition, 2000, 'easeInOutExpo')
      }
    }, 500)
  }, [setSelectedYear, setActiveCityId, setSelectedCluster, reset, getYearData, calculateOptimalCameraPosition, animateCameraToPosition])

  const handlePlayPause = useCallback(() => {
    if (isPlaying) {
      pause()
      // Don't lock user interaction - allow free movement after pausing
      setUserInteracting(false)
