      '#fbbf24', // bright amber
      '#f87171', // bright red
      '#a78bfa', // bright purple
      '#22d3ee', // bright cyan
      '#fb923c', // bright orange
      '#ec4899', // bright pink
      '#10b981', // emerald
      '#06b6d4', // sky
      '#8b5cf6', // violet
      '#f59e0b', // amber
      '#ef4444', // red
      '#14b8a6', // teal
      '#6366f1', // indigo
      '#f97316', // orange
    ]

